import { Button, FormControl, Input, Modal, ModalBody, ModalCloseButton, ModalContent, ModalFooter, ModalHeader, ModalOverlay, useDisclosure, useToast } from '@chakra-ui/react'
import React, { useState } from 'react'
import { ChatState } from '../../Context/ChatProvider';

const GroupChatModal = ({ children }) => {
   const { isOpen, onOpen, onClose } = useDisclosure();
   const [groupChatName, setGroupChatName] = useState();
   const [selectedUsers, setSelectedUsers] = useState();
   const [searchResult, setSearchResult] = useState();
   const [loading, setLoading] = useState();
   const [search, setSearch] = useState();

   const toast = useToast();

   const { user, chat, setChats } = ChatState();

   const handleSearch = async (query) =>{
      setSearch(query);
      if(!query){
         return;
      }
      try {
         setLoading(true);
         const config = {
            headers:{
               Authorization: `Bearer ${user.token}`,
            },
         };

         const {data} = await axios.get(`/api/user?search${search}`, config);
         setLoading(false);
         setSearchResult(data);
      } catch (error) {
         toast({
            title: "Произошла ошибка!",
            description
         })
      }
   };

   const handleSubmit = () =>{};
   return (
      <>
         <span onClick={onOpen}>{children}</span>

         <Modal isOpen={isOpen} onClose={onClose}>
            <ModalOverlay />
            <ModalContent>
               <ModalHeader
               fontSize="35px"
               fontFamily="inherit"
               display="flex"
               justifyContent="center"
               >Добавить групповой чат</ModalHeader>
               <ModalCloseButton />
               <ModalBody display="flex" flexDir="column" alignItems="center">
                  <FormControl>
                     <Input
                        placeholder='Название чата'
                        mb={3}
                        onChange={(e) => setGroupChatName(e.target.value)}
                     />   
                  </FormControl>
                  <FormControl>
                     <Input
                        placeholder='Добавьте пользователя'
                        mb={1}
                        onChange={(e) => handleSearch(e.target.value)}
                     />
                  </FormControl>
                  { }
                  { }
               </ModalBody>
               <ModalFooter>
                  <Button colorScheme='blue' onClick={handleSubmit}>
                     Создать групповой чат
                  </Button>
               </ModalFooter>
            </ModalContent>
         </Modal>
      </>);
}

export default GroupChatModal
