import React from 'react'
import { ChatState } from '../Context/ChatProvider';
import { Box, Text } from '@chakra-ui/react';

const NewsBox = () => {
	const { selectedChat } = ChatState();
	return (
		<Box display={{ base: selectedChat ? "flex" : "none", md: "flex" }}
			alignItems="center"
			flexDir="column"
			p={3}
			bg="white"
			w={{ base: "100%", md: "30%" }}
			borderRadius="lg"
			borderWidth="1px"
		>
			<Text>Как прошел финал конкурса «Мисс ФММП – 2023»</Text>
			<Text>На ФММП 15 мая состоялся финал конкурса «Мисс ФММП – 2023». Тематикой мероприятия стал бал-маскарад под лозунгом «Сними маску, покажи себя настоящей».</Text>
		</Box>
	)
}

export default NewsBox;
