import React from 'react'
import { ChatState } from '../Context/ChatProvider';
import { Box, Image, Text } from '@chakra-ui/react';

const NewsBox = () => {
	const { selectedChat } = ChatState();
	return (
		<Box display={{ base: selectedChat ? "flex" : "none", md: "flex" }}
			alignItems="center"
			flexDir="column"
			p={3}
			bg="white"
			w={{ base: "100%", md: "30%" }}
			borderRadius="lg"
			borderWidth="1px"
		>	
			<Image src=''></Image>
			<Text>Проект «Время твоих возможностей 2.0»</Text>
			<Text>Заместитель председателя студенческого совета приняла участие в молодежном проекте «Время твоих возможностей 2.0»</Text>
		</Box>
	)
}

export default NewsBox;
