import React from 'react'
import { ChatState } from '../Context/ChatProvider';
import { Box, Text } from '@chakra-ui/react';

const NewsBox = () => {
	const { selectedChat } = ChatState();
	return (
		<Box display={{ base: selectedChat ? "flex" : "none", md: "flex" }}
			alignItems="center"
			flexDir="column"
			p={3}
			bg="white"
			w={{ base: "100%", md: "30%" }}
			borderRadius="lg"
			borderWidth="1px"
		>
			<Text>Студсовет БНТУ посетил «Территорию Коммуникаций»</Text>
			<Text><a href="https://times.bntu.by/news/13535-studsovet-bntu-posetil-territoriyu-kommunikacij" class="newsName">Студсовет БНТУ посетил «Территорию Коммуникаций»</a></Text>
		</Box>
	)
}

export default NewsBox;
