import { ViewIcon } from '@chakra-ui/icons';
import { Box, Button, FormControl, IconButton, Input, Modal, ModalBody, ModalCloseButton, ModalContent, ModalFooter, ModalHeader, ModalOverlay, useDisclosure, useToast } from '@chakra-ui/react';
import React, { useState } from 'react'
import { ChatState } from '../../Context/ChatProvider';
import UserBadgeItem from '../UserAvatar/UserBadgeItem';
const UpdateGroupChatModal = ({ fetchAgain, setFetchAgain }) => {
   const { isOpen, onOpen, onClose } = useDisclosure();
   const [groupChatName, setGroupChatName] = useState();
   const [search, setSearch] = useState("");
   const [searchResult, setSearchResult] = useState([]);
   const [loading, setLoading] = useState(false);
   const { selectedChat, setSelectedChat, user } = ChatState();
   const [renameLoading, setRenameLoading] = useState();
   const toast = useToast();

   const handleRemove = () => { };

   const handleRename = async () => {

   };

   const handleSearch = async () => {
      if (!groupChatName) { return; }
      try {
         setRenameLoading(true);
         const config = {
            headers: {
               Authorization: `Bearer ${user.token}`,
            },
         }
         const { data } = await axios.put("/api/chat/rename",
            {
               chatId: selectedChat._id,
               chatName: groupChatName,
            },
            config);
            setSelectedChat(data);
            setFetchAgain(!fetchAgain);
            setRenameLoading(false);
      } catch (error) {
         toast({
            title: "Произошла ошибка!",
            description: error.messa
         })

      }
   };

   return (
      <>
         <IconButton display={{ base: "flex" }} icon={<ViewIcon />} onClick={onOpen}></IconButton>
         <Modal isOpen={isOpen} onClose={onClose} isCentered>
            <ModalOverlay />
            <ModalContent>
               <ModalHeader>{selectedChat.chatName}</ModalHeader>
               <ModalCloseButton />
               <ModalBody>
                  <Box w="100%" display="flex" flexWrap="wrap" pb={3}>
                     {selectedChat.users.map(u => (<UserBadgeItem
                        key={user._id} user={u}
                        handleFunction={() => handleRemove(u)} />))}
                  </Box>
                  <FormControl display="flex">
                     <Input
                        placeholder='Название чата'
                        mb={3}
                        onChange={(e) => setGroupChatName(e.target.value)}
                     />
                  </FormControl>
                  <Button
                     variant="solid"
                     colorScheme="teal"
                     ml={1}
                     isLoading={renameLoading}
                     onClick={handleRename}
                  >Обновить
                  </Button>
                  <FormControl>
                     <Input
                        placeholder='Добавьте пользователя'
                        mb={1}
                        onChange={(e) => handleSearch(e.target.value)}
                     />
                  </FormControl>
               </ModalBody>
               <ModalFooter>
                  <Button onClick={() => handleRemove(user)} colorScheme="red">Leave Group</Button>
               </ModalFooter>
            </ModalContent>
         </Modal>
      </>
   )
}

export default UpdateGroupChatModal
