         setSelectedChat(data);
         setLoadingChat(false);
         onClose();
      } catch (error) {
         toast({
            title: "Ошибка загрузки чата",
            description: error.message,
            status: "error",
            duration: 5000,
            isClosable: true,
            position: "bottom-left",
         });
      }
   }
   return (
      <div>
         My Chats
      </div>
   )
}

export default MyChats;
